script:
frappe.ui.form.on("Customer", "onload_post_render", function (frm) {
    if (frm.doc.customer_type == 'Client' || frm.doc.customer_type == 'Owner') {
        frm.clear_custom_buttons()
        frm.dashboard.hide()
    }

});

frappe.ui.form.on("Customer", "onload", function (frm) {
    if (frappe.get_prev_route()[1] == "Lead") {
        frm.set_value('customer_type', 'Client')
    }
});
